package week1.day2.homeAssignments;

public class CheckNumberIsPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int version = 0;
		if (version>0)			
			System.out.println(version + " is Positive"); //Check if he number is less than positive.
		else if (version<0)
			System.out.println(version + " is Negative"); //Check if he number is less than Negative.
		else
			System.out.println(version + " is Zero");	//for zero.

	}

}
